class Solution {
    public void wiggleSort(int[] nums) {
        if (nums == null || nums.length == 0) return;
        int n = nums.length;
        int[] temp = new int[n];
        for (int i = 0; i < n; i++) temp[i] = nums[i];
        int pivot = n / 2, l = 0, r = temp.length - 1;
        while (l < r) {
            int mid = helper(temp, l, r);
            if (mid == pivot) break;
            if (mid < pivot) {
                l = mid + 1;
            } else {
                r = mid - 1;
            }
        }
        int[] ans = new int[n];
        Arrays.fill(ans, temp[pivot]);
        if (n % 2 == 0) {
            l = ans.length - 2;
            r = 1;
            for (int i = 0; i < nums.length; i++) {
                if (nums[i] < temp[pivot]) {
                    ans[l] = nums[i];
                    l -= 2;
                } else if (nums[i] > temp[pivot]) {
                    ans[r] = nums[i];
                    r += 2;
                }
            }

        } else {
            // 1 5 1 1 6 4 7
            l = ans.length - 1;
            r = 1;
            for (int i = 0; i < nums.length; i++) {
                if (nums[i] < temp[pivot]) {
                    ans[l] = nums[i];
                    l -= 2;
                } else if (nums[i] > temp[pivot]) {
                    ans[r] = nums[i];
                    r += 2;
                }
            }
        }

        for (int i = 0; i < nums.length; i++) {
            nums[i] = ans[i];
        }

    }
    private int helper(int[] nums, int l, int r) {
        int pivot = l;
        for (int i = l; i < r; i++) {
            if (nums[i] < nums[r]) {
                swap(nums, i, pivot++);
            }
        }
        swap(nums, pivot, r);
        return pivot;
    }
    private void swap(int[] nums, int l, int r) {
        int temp = nums[l];
        nums[l] = nums[r];
        nums[r] = temp;
    }
}
