    class Solution(object):
        def reverse(self, start, end):
            low, high = start, end
            while low < high:
                self.nums[low], self.nums[high] = self.nums[high], self.nums[low]
                low += 1
                high -= 1

        def getK(self, n):
            k, square = 1, 3
