public:

    //finds kth element using passed random pivot.
    int quick_select(vector<int>& v,int l,int r,int pivot)
    {
        swap(v[r],v[pivot]);
        int i,j;
        i=j=l;
        while(j<=r)
        {
            if(v[j]<v[r])
            {
                swap(v[i],v[j]);
                i++;
            }
            j++;
        }
        swap(v[i],v[r]);
        return i;
    }


    void wiggleSort(vector<int>& v) {
        int n=v.size();
        int k=(n-1)/2;
        int l=0,r=n-1;
        //finding middle index using randomized quick sort.
        while(1)
        {
            int pivot=(rand()%(r-l+1))+l;
            int x=quick_select(v,l,r,pivot);
            if(x==k)break;
            else if(x>k)r=x-1;
            else l=x+1;
