class Solution {
public:
    void wiggleSort(vector<int>& nums) {
        int n = nums.size(), m = (n + 1) >> 1;
        if (n <= 1) return;
        int mid = findkthsmallest(nums, m);
        vector<int> aux (nums.begin(), nums.end());

        for (int i = 0, l = 0, r = n - 1; i < r; ){
            if (aux[i] == mid) i ++;
            else if (aux[i] > mid) swap(aux[i --], aux[r --]);
            else swap(aux[l ++], aux[i ++]);
        }

        // for (int i = 0; i < n; i ++) cout << aux[i] << " ";
        for (int i = 0, j = m - 1; j >= 0; i += 2, j --) nums[i] = aux[j];
        for (int i = 1, j = n - 1; j >= m; i += 2, j --) nums[i] = aux[j];
    }

    int findkthsmallest(vector<int>& nums, int k){
        int l = 0, r = nums.size() - 1;

        while (l < r){
            int mid = partition(nums, l, r);
            if (mid == k) break;
            else if (mid < k) l = mid + 1;
            else r = mid - 1;
        }
        return nums[k];
    }

    int partition(vector<int>& nums, int l, int r){
        int i = l, j = r + 1;
        while (i < j){
            while(i < r && nums[++ i] <= nums[l]) ;
            while(j > l && nums[-- j] >= nums[l]) ;
            if (i < j) swap(nums[i], nums[j]);
        }
        swap(nums[l], nums[j]);
        return j;
    }
};
