```
class Solution:
    def wiggleSort(self, nums: List[int]) -> None:
        """
        Do not return anything, modify nums in-place instead.
        """
        if len(nums) <= 1:
            return nums 
        n = len(nums)
        l = 0
        r = n
        m = (n+1) // 2

        self.nums = nums
        self.n = n

        while l < r:
            p = self.get(m)

            lcnt = 0
            ecnt = 0
            hcnt = 0
            for i in range(l, r):
                v = self.get(i)
                if v < p:
                    lcnt += 1
                elif v == p:
                    ecnt += 1
                else:
                    hcnt += 1

            lc = 0
            for i in range(l, r):
                if self.get(i) < p:
                    self.swap(i, lc + l)
                    lc += 1
            assert lc == lcnt

            ec = 0
            for i in range(l + lcnt, r):
                if self.get(i) == p:
                    self.swap(i, ec + lcnt + l)
                    ec += 1
            assert ec == ecnt

            assert l <= m and m < r
            if m < l + lcnt:
                r = l + lcnt
            elif m < l + lcnt + ecnt:
                break
            else:
                l = l + lcnt + ecnt


    def swap(self, i, j):
        self.nums[self.im_re(i)], self.nums[self.im_re(j)] = self.nums[self.im_re(j)], self.nums[self.im_re(i)]


    def get(self, i):
        return self.nums[self.im_re(i)]


    def im_re(self, i):
		"""
		This is a mapping that allows us to do the imaginary problem:
			Find median and put everything below it on the left and everything above it on the right (This is a well-known O(n) problem)
		"""
        n = self.n
        m = (n + 1) // 2
        if i < m:
            return (m - i - 1) * 2
        else:
            return (n - i - 1) * 2 + 1


    def disp(self):
        print('re:', self.nums, 'im:', [self.get(i) for i in range(self.n)])
```

