class Solution {

    /**
     * @param Integer[] $nums
     * @return NULL
     */
    function wiggleSort(&$nums) {
        sort($nums);
        $count = count($nums);
        $data1 = [];
        $data2 = [];
        $k = 0;
        $check = -1;
        $count1 = ceil($count / 2);
        if ($count % 2 != 0) {
            $k = 1;
            $check = $nums[0];
            $count1 = ceil($count / 2);
        }
        for ($i = $k; $i < $count1; $i++) {
            $data1[] = $nums[$i];
        }
        for ($i = $count1; $i < $count; $i++) {
            $data2[] = $nums[$i];
        }
        $nums = [];
        for ($i = count($data1) - 1; $i >= 0; $i--) {
            $nums[] = $data1[$i];
            $nums[] = $data2[$i];
        }

        if ($check != -1) {
            array_push($nums, $check);
        }

        return $nums;
    }
}

