class Solution {
    public void wiggleSort(int[] nums) {
        Arrays.sort(nums);
        int arr[] = new int[nums.length];

        int i=nums.length-1, j=1;
        while(j<nums.length){
            arr[j]=nums[i];
            i--;
            j=j+2;
        }

        j=0;
        while(i>=0){
            arr[j]=nums[i];
            i--;
            j=j+2;
        }

        for(int it=0; it<nums.length; it++){
            nums[it]=arr[it];
        }
    }
}
