class Solution {
public:map<char,vector>mp;
    int characterReplacement(string s, int k) {
        map<char,vector<int>>mp;

        for(int i = 0;i<s.size();i++){
            mp[s[i]].push_back(i);
        }


        int ans = 0;
        for(char c = 'A';c<='Z';c++){
            vector<int>gaps;
            if(mp[c].size() == 0)continue;
            int extra = mp[c][0];

            for(int j = 1;j<mp[c].size();j++){
                if(j == mp[c].size() - 1){
                    // gaps.push_back(s.size() - mp[c][j] - 1);
                    extra += s.size() - mp[c][j] - 1;
                }
                gaps.push_back(mp[c][j] - mp[c][j - 1] - 1);
            }

            //jitne poore liye + 1
            int low = 0,high = 0;
            int max_take = 0;

            int tot = extra;
            for(auto j : gaps){
                tot+=j;
            }
            // cout << tot << '\n';
            int cur = 0;

            while(low < gaps.size()){
                while(high<gaps.size() && cur + gaps[high] <= k){
                    cur += gaps[high];
                    high++;
                    max_take++;
                }
                ans = max(ans,max_take + 1 + cur + min(tot - cur,(k - cur)));

                if(high > low){
                    cur -= gaps[low];
                    max_take--;                    
                }
                low++;
                high = max(high,low);
            }

            ans = max(ans,1 + min(k,tot));
        }


        return ans;
    }
};
```

