class Solution:
    def findMaxAverage(self, nums: List[int], k: int) -> float:
        maxAvg = float('-inf')
        windowSum, windowStart = 0.0,0
        for windowEnd in range(len(nums)):
            windowSum += nums[windowEnd]
            if windowEnd >= k-1:
                avg = windowSum / k
                maxAvg = max(maxAvg,avg)
                windowSum -= nums[windowStart]
                windowStart += 1

        return maxAvg
