class Solution {
    public int characterReplacement(String s, int k) {
        int[] freq = new int[26];
        int windowEnd = 0, windowStart = 0, maxFreqLetterLength = 0, longest = 0;
        for (; windowEnd < s.length(); windowEnd++) {
            freq[s.charAt(windowEnd) - 'A']++;
            maxFreqLetterLength = Math.max(maxFreqLetterLength, freq[s.charAt(windowEnd) - 'A']);

            int currentSegmentedWindow = windowEnd - windowStart + 1;
            if (currentSegmentedWindow - maxFreqLetterLength > k) {
                // replacement done beyond k letters
                // hence decrement the current freq
                freq[s.charAt(windowStart) - 'A']--;
                windowStart++;
            }
            longest = Math.max(longest, windowEnd - windowStart + 1);
        }
        return longest;
    }
}
