class Solution:
    def characterReplacement(self, s: str, k: int) -> int:
        d = {}
        slow = 0
        ans = 0
        for fast in range(len(s)):
            if s[fast] in d:
                d[s[fast]] += 1
            else:
                d[s[fast]] = 1

            # get max of the window and check if 
            # length of the window - max valued key <= k

            if ((fast-slow+1) - max(d.values())) <= k:
                pass
            else:
                # shrink the window
                while ((fast-slow+1) - max(d.values())) > k:
                    d[s[slow]] -= 1
                    if d[s[slow]] == 0:
                        del d[s[slow]]
                    slow += 1
            # calculate the length of the window
            ans = max(ans, fast - slow + 1)
        return ans

