int divide(int dividend, int divisor) {
        // I will change both dividend and divisor to be negative

        bool sign = true; // true -> result positive; false -> result negative;
        if (dividend > 0) {
            sign = !sign;
            dividend = -dividend;
        }
        if (divisor > 0){
            sign = !sign;
            divisor = -divisor;
        }

        // now dividend and divisor are both smaller than or equal to 0
        int res = 0;
        while (dividend <= divisor){
            int i = 0;
            int x = divisor;
            // This while loop finds i 
            // such that  2^(i+1) * divisor < dividend <= 2^i * divisor
            while (true) {
                if (x < - (1<<30)) break; // keep x + x in integer range
                if (dividend > x + x) break;
                x = x + x;
                i++;
            }
            res += 1<<i;
            dividend -= x;
        }
        // res should be larger than 0
        // If res == INT_MIN && sign, 
        // it indicates the result is -INT_MIN, which overflows;
        if (res == INT_MIN) return sign?INT_MAX:INT_MIN;
        return sign? res:-res;
    }
