class Solution(object):
    def divide(self, a, b):
        INT_MAX = 2147483647 # 2**31 - 1
        positive = (a >= 0) == (b >= 0) # Positive only if a and b the same sign
        a, b = abs(a), abs(b)
        ans = 0
        while a >= b:
            multiply = 1 # 1 2 4 8 16...
            sum = b
            while (sum + sum) <= a:
                sum += sum
                multiply += multiply
            ans += multiply
            a -= sum
        ans = ans if positive else -ans
        return min(ans, INT_MAX)
