using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ConsoleApplication3
{
    class Program
    {
        public static void Divide(long dividend , long divisor)
        {
            if (dividend == 0 || Math.Abs(dividend) < Math.Abs(divisor))
            {
                Console.Write(0);
            }
            else 
            {
                int devidend_isGreater_than_0 = 1;
                int divisor_isGreater_than_0 = 1;
                if (dividend < 0) 
                {
                    dividend = dividend * (-1);
                    devidend_isGreater_than_0 = -1;
                }
                if (divisor < 0) 
                {
                    divisor = divisor * (-1);
                    divisor_isGreater_than_0 = -1;
                }

                long count = 0;
                for (long i = 0; i < long.MaxValue; i++) 
                {
                    dividend = dividend - divisor;
                    if (dividend >= 0)
                    {
                        count++;
                    }
                    else 
                    {
                        break;
                    }
                }
                if (devidend_isGreater_than_0 * divisor_isGreater_than_0 < 0) 
                {
                    count *= -1;
                }

                Console.Write(count);
            }
        }
        static void Main(string[] args)
        {
            string dividend, divisor;
            dividend = Console.ReadLine();
            long DIVIDEND = long.Parse(dividend);

            long DIVISOR;
            do
            {
                divisor = Console.ReadLine();
                DIVISOR = long.Parse(divisor);
            } while (DIVISOR == 0);

            Divide(DIVIDEND, DIVISOR);
            Console.ReadKey();
        }
    }
}


