class Solution {
public:
    int total = 0;
    int sumNumbers(TreeNode* root) {
        helper(root, 0);
        return total;
    }

    void helper(TreeNode* node, int val) {
        // is this a leaf node
        if(!node->left && !node->right) {
            total += val*10+node->val;
            return;
        }
        if(node->left){
            helper(node->left, val*10+node->val);
        }
        if(node->right){
            helper(node->right, val*10+node->val);
        }
    }
};
