```
# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right

class Solution:
    def sumNumbers(self, root: Optional[TreeNode]) -> int:

        def calc(node, val):

            ## base case aka stop condition:
            # generate decimal value on leaf node
            if not node.left and not node.right:
                yield (val * 10 + node.val)

            ## general cases:
            # visit each non-leaf nodes
            for child in (node.left, node.right):
                if child:
                    yield from calc(child, val * 10 + node.val)

        # -------------------------------------------------------------
        # sum of root-to-leaf numbers
        return sum( calc(root, 0) )
