class Solution:
    def sumNumbers(self, root: TreeNode) -> int:
        summation = 0
        if root is None:
            return summation

        stack = [(root, root.val)]

        while len(stack) != 0:
            node, running_sum = stack.pop()

            if node.left is None and node.right is None:
                summation += running_sum
                continue

            if node.left:
                new_sum = int(str(running_sum) + str(node.left.val))
                stack.append((node.left, new_sum))
            if node.right:
                new_sum = int(str(running_sum) + str(node.right.val))
                stack.append((node.right, new_sum))                

        return summation
