class Solution {
    var s = 0

    func sumNumbers(_ root: TreeNode?) -> Int {
        var p = 0
        f(root, &p)

        return s
    }

    func f(_ root: TreeNode?, _ p: inout Int) {
        guard let r = root else { return }

        p = p * 10 + r.val
        if r.left == nil && r.right == nil {
            s += p
        }

        f(r.left, &p)
        f(r.right, &p)
        p -= r.val
        p /= 10
    }

}

