        }
        numtillnow*=10;
        numtillnow+=root->val;
        if(root->left==NULL && root->right==NULL)
        {
            ans+=numtillnow;
            return;
        }
        add(root->left,numtillnow);
        add(root->right,numtillnow);
        return;
    }
