        data = data * 10 + root.val; // Calculate the new value by appending the current node's value.

        if (root.left == null && root.right == null) {
            sum += data; // If it's a leaf node, add the value to the sum.
        }

        number(root.left, data); // Recurse on the left subtree with the updated value.
        number(root.right, data); // Recurse on the right subtree with the updated value.
    }
}

