 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public int sumNumbers(TreeNode root) {
        return dfs(root, 0);
    }

    int dfs(TreeNode root, int sum)
    {
        if(root == null)
            return 0;

        sum = (sum * 10) + root.val;

        int left = dfs(root.left, sum);
        int right = dfs(root.right, sum);

        return (left+right) == 0 ? sum : left+right;    
    }
}
