// // Freq(ME) > floor(n/k)
vector<int> majorityElementGeneralised(vector<int> arr, int k) {
   int n = arr.size();

   // Map of Majority Element(ME), Count
   // Count is not Frequency in array.
   // For checking Freq(ME) > floor(n/k) we run another loop
   // on counters.first element or on ME's
   map<int, int> counters;

   for (auto element : arr) {
      // If element present in map counters,
      // Increment that counter value
      if (counters.count(element))
         ++counters[element];

      // If element NOT present in map counters
      // Add it in counters
      // Which means that we created a new counter for that candidate
      // ONLY if counters.size() < k - 1.
      // Which means that if N = 4 we can only have 3 counters. Explained above in detail.
      else if (counters.size() < k - 1)
         ++counters[element];

      // This means that we have max number of candidates
      // OR
      // Max number of counters and we have a new element
      // Simply decrement all counters by 1 (Explained above)
      // BUT
      // If a counter == 0. Not required cause if later that element becomes Majority we can always initialise it by 1.
      // So remove from map counters.
      else {
         //Iterate over all counters and decrement by 1
         //If counter is 0 for an element in Map Counter
         //It is not required as instead of that element,
         //we will add a new element
         auto it = counters.begin();
         while (it != counters.end()) {
            int key = it->first;
            if (it->second == 1) {
               it = counters.erase(it);
            }
            else {
               --counters[key];
               ++it;
            }
         }
      }
   }

   int validFreq = floor(n / k);

   vector<int> result = {};

   // Use map counters as a frequency map by initialising counter.first or Majority element frequency to 0.
   for (auto element : arr) {
      if (counters.count(element))
         counters[element] = 0;
   }

   // If found in array update frequency
   for (auto element : arr) {
      if (counters.count(element))
         ++counters[element];
   }

   // Check is valid Majority Element
   for (auto me : counters)
      if (me.second > validFreq)
         result.push_back(me.first);

   return result;
}

vector<int> majorityElement(vector<int>& nums) {
   return majorityElementGeneralised(nums, 3);
}
```

