        if(nums.length <= k-1){
            Set<Integer> set = new HashSet<>(k);
            for(int n : nums){
                set.add(n);
            }
            return new ArrayList<>(set);
        }

        Integer[] counts = new Integer[k-1];
        Arrays.fill(counts,0);
        Integer[] candidates = new Integer[k-1];

        for(int n : nums){
            populateCandidate(counts,candidates,n,k-1);
        }

        Arrays.fill(counts,0);
        for(int n : nums){
            updateCount(counts,candidates,n,k-1);
        }

        int threshold = nums.length/k;
        for(int i=0;i<k-1;i++){
            if(counts[i] > threshold){
                result.add(candidates[i]);
            }
        }
        return result;
    }

    private boolean updateCount(Integer[] counts,Integer[] candidates,int n,int k){
        for(int i=0;i<k;i++){
            if(candidates[i] != null && candidates[i] == n){
                counts[i]++;
                return true;
            }
        }
        return false;
    }

    private void populateCandidate(Integer[] counts,Integer[] candidates,int n,int k){

        if(updateCount(counts,candidates,n,k)){
            return;
        }

        for(int i=0;i<k;i++){
            if(counts[i] == 0){
                candidates[i] = n;
                counts[i]++;
                return;
            }
        }
        for(int i=0;i<k;i++){
            counts[i]--;
        }
    }

}
