class Solution {
public:
    int c(int i,int j,vector<vector<int>>&dp,vector<vector<int>>&v){
        if(dp[i][j]!=-1)return dp[i][j];
        int n=v.size();
        int m=v[0].size();
        int ans=1;
        int dx[4]={1,-1,0,0};
        int dy[4]={0,0,1,-1};
        for(int k=0;k<4;k++){
            int nx=i+dx[k];
            int ny=j+dy[k];
            if(nx>=0 && ny>=0 && nx<n && ny<m && v[i][j]<v[nx][ny]){
                ans = max(ans , 1 + c(nx,ny,dp,v));
            }
        }
        dp[i][j]=ans;
        return ans;
    }
    int longestIncreasingPath(vector<vector<int>>& v) {
        int ans=1;
        int n=v.size();
        int m=v[0].size();
        vector<vector<int>>dp(n,vector<int>(m,-1));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans = max(ans,c(i,j,dp,v));
            }
        }
        return ans;
    }
};
