solutions.content:
idea is simple , traverse in all 4 valid directions where its possible and make mark it as visited,
store answers for all cells which you have scene so far essencially catching the answers for cells
```
int x[] = {-1,0,1,0};      // these will guide us for our next direction
int y[] = {0,1,0,-1};     // here direction are in order up, right , down , left , ie. clockwise from up
class Solution {
public:
    int n,m;    vector<vector<int>> dp; vector<vector<bool>> vis;
    int ans=0;

    int dfs(vector<vector<int>> &mat , int i, int j)
    {
        if(dp[i][j])    return dp[i][j];
        vis[i][j] = true;
        int small_ans = 0;
        for(int k=0 ; k<4 ; k++)
        {
            int newi = i+x[k];
            int newj = j+y[k];
			// now make sure newi and newj are within bounds and valid , valid means that we have not previously visited in current DFS chain
			//and matric of newi,j as greater than our previous location.
            if(newi>=0 and newj>=0 and newi<n and newj<m and mat[newi][newj]>mat[i][j] and vis[newi][newj] == false)
               small_ans = max(small_ans , dfs(mat,newi,newj) + 1);
        }
        dp[i][j] = max(1, small_ans);
        vis[i][j] = false;
        ans = max(ans , dp[i][j]);
        return dp[i][j];
    }

    int longestIncreasingPath(vector<vector<int>>& mat) {
        n = mat.size();
        if(n==0)    return 0;
        m = mat[0].size();
        dp = vector<vector<int>>(n, vector<int>(m));
        vis = vector<vector<bool>>(n,vector<bool>(m));
        for(int i=0 ; i<n ; i++)
        {
            for(int j=0 ; j<m ; j++)
            {
                dfs(mat,i,j);
            }
        }
    return ans;        
    }
};

