    const memo = {};

    function dfs(row, col) {

        if(memo[`${row},${col}`] !== undefined) {
            return memo[`${row},${col}`];
        }

        let count = 1;

        if(row + 1 < matrix.length && matrix[row+1][col] > matrix[row][col]) {
            count = Math.max(dfs(row+1, col) + 1, count);
        }

        if(row - 1 >= 0 && matrix[row-1][col] > matrix[row][col]) {
            count = Math.max(dfs(row-1, col) + 1, count);
