class Solution {

    public class Pair{
        int r,c;
        Pair(int r, int c){
            this.r = r;
            this.c = c;
        }
    }

    public int longestIncreasingPath(int[][] arr) {
        int n = arr.length;
        int m = arr[0].length;
        int[][] dir = {{0,1},{1,0},{0,-1},{-1,0}};
        int[][] inDegree = new int[n][m];
        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < m ; j++){
                for(int d = 0 ; d < dir.length; d++){
                    int r = i + dir[d][0];
                    int c = j + dir[d][1];
                    if(r < n && c < m && r >= 0 && c >= 0 && arr[r][c] > arr[i][j]){
                        inDegree[r][c]++;
                    }
                }
            }   
        }

        Queue<Pair> q = new LinkedList<>();
        for(int i = 0 ; i < n ; i++){
            for(int j = 0 ; j < m ; j++){
                if(inDegree[i][j] == 0) q.add(new Pair(i,j));
            }   
        }

        int lev = 1;

        while(!q.isEmpty()){
            int size = q.size();
            while(size-- > 0){
                Pair rp = q.remove();
                for(int d = 0 ; d < dir.length; d++){
                    int r = rp.r + dir[d][0];
                    int c = rp.c + dir[d][1];
                    if(r < n && c < m && r >= 0 && c >= 0 && arr[r][c] > arr[rp.r][rp.c]){
                        if(--inDegree[r][c] == 0) q.add(new Pair(r,c));
                    }
                }
            }
            if(!q.isEmpty()) lev++;
        }
        return lev;
    }
}
