     public int BFS_kahnsAlgo(int[][] matrix){
        int n=matrix.length;
        if(n==0) return 0;
        int m=matrix[0].length;
        int[][] indegree=new int[matrix.length][matrix[0].length];
        LinkedList<int[]> que=new LinkedList<int[]>();
        for(int i=0;i<n;i++){
