                    int c=j+dir[k][1];
                    if(r>=0 && c>=0 && r<n && c<m && matrix[r][c]<matrix[i][j]){
                       indegreeCount++;
                    } 
                }
                indegree[i][j]=indegreeCount;
                if(indegree[i][j]==0) que.addLast(new int[]{i,j});
            }
        }
        int length=0;
        while(!que.isEmpty()){
            int size=que.size();
            while(size-->0){
                int[] rPair=que.removeFirst();
                int i=rPair[0];
                int j=rPair[1];
                for(int k=0;k<4;k++){
                    int r=i+dir[k][0];
                    int c=j+dir[k][1];
                    if(r>=0 && c>=0 && r<n && c<m && matrix[r][c]>matrix[i][j] && --indegree[r][c]==0){
                        que.addLast(new int[]{r,c});
                    }
                }
            }
            length++;
        }
        return length;
    }

