class Solution {
public:
    int dp[200][200]={0};
    int m, n;
    int dfs(vector<vector<int>>& matrix, int i, int j, int p)
    {
        if(i<0 || j<0 || i>=m || j>=n || matrix[i][j]<=p)
            return 0;
        if(dp[i][j])
            return dp[i][j];
        p=matrix[i][j];
        return dp[i][j] = 1+ max({dfs(matrix, i+1, j, p),
                      dfs(matrix, i-1, j, p),
                      dfs(matrix, i, j+1, p),
                      dfs(matrix, i, j-1, p)});
    }
    int longestIncreasingPath(vector<vector<int>>& matrix) {
        int ans=0;
        m=matrix.size();
        n=matrix[0].size();
        for(int i=0;i<m;i++)
            for(int j=0;j<n;j++)
            ans=max(ans, dfs(matrix, i, j, -1)); 
        return ans;
    }
};
