                return visited[i][j]

            up,down,left,right = 0,0,0,0
            if i+1<m and mat[i][j]<mat[i+1][j]:
                down=fun(i+1,j)

            if j+1<n and mat[i][j]<mat[i][j+1]:
                right=fun(i,j+1)

            if i-1>=0 and mat[i][j]<mat[i-1][j]:
                up=fun(i-1,j)  

            if j-1>=0 and mat[i][j]<mat[i][j-1]:
                left=fun(i,j-1)

            maxpath = 1+max(up,down,left,right)
            visited[i][j] = maxpath

            return maxpath

        ans = 1
        for i in range(m):
            for j in range(n):
                ans = max(ans,fun(i,j))

        return ans

