**Intution:**

* Graph topological sort from biggest to smallest

```
class Solution {
public:
    int longestIncreasingPath(vector<vector<int>>& matrix) {
        int n = matrix.size();
        int m = n ? matrix[0].size() : 0;
        vector<vector<int>> indegree(n, vector<int>(m,0));

        vector<int> xdiff = {0,1,0,-1}; 
        vector<int> ydiff = {-1,0,1,0}; 


        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                for(int k =0;k<4;++k){
                    int nx=i+xdiff[k];
                    int ny=j+ydiff[k];
                    if(nx>=0 && ny>=0 && nx<n && ny<m)
                    {
                        if(matrix[nx][ny]>matrix[i][j])
                            indegree[i][j]++;
                    }
                }
            }
        }

        queue<pair<int,int>> q;
        int ans =0;
        for(int i=0;i < n;++i){
            for(int j=0;j < m;++j){
                if(indegree[i][j] == 0){
                    q.push({i,j});
                }
            }
        }

        while(!q.empty())
        {
            int size=q.size();
            for(int i=0;i<size;i++)
            {
                pair<int,int> node=q.front();
                q.pop();
                 for(int k =0;k<4;++k){
                     int nx = node.first+xdiff[k];
                     int ny = node.second+ydiff[k];
                    if(nx>=0 && ny>=0 && nx<n &&ny<m ){
                        if(matrix[node.first][node.second] > matrix[nx][ny] && --indegree[nx][ny] == 0){
                            q.push({nx,ny});
                        }
                    }
                }
            }
            ans++;
        }
        return ans;
    }
};
