class Solution {
public:
    int dp[205][205];
    int d[4][2]={{-1,0},{0,-1},{1,0},{0,1}}; // For all 4 Directions
    int dfs(vector<vector<int>>&a , int i, int j , int c,int n, int m)
    {
        if(dp[i][j] != -1)
            return dp[i][j]+c;
        int c1=c;
        for(auto l:d)
        {
            int x=i+l[0];
            int y=j+l[1];
            if(x>=0 && x<n && y>=0 && y<m)
             if(a[x][y]>a[i][j])
             c1=max(c1,dfs(a,x,y,c+1,n,m));
        }
     dp[i][j]=c1-c;
        return c1;  
    }

    int longestIncreasingPath(vector<vector<int>>& a) {
        int n=a.size();
        int m=a[0].size();
        int as=-1;
        memset(dp,-1,sizeof dp);
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
              as=max(as,dfs(a,i,j,1,n,m)); 
            }
        }
        return as;
    }
};
