class Solution {
    static int[][] DIRECTIONS = new int[][]{{1, 0}, {-1, 0}, {0, 1}, {0, -1}};
    int rows;
    int cols;

    int[][] cache;
    public int longestIncreasingPath(int[][] matrix) {
        rows = matrix.length;
        if(rows == 0) return 0;

        cols = matrix[0].length;

        cache = new int[rows][cols];

        int maxIncreasingLengthPath = 0;
        for(int row = 0; row < rows; row++){
            for(int col = 0; col < cols; col++){
                maxIncreasingLengthPath = Math.max(maxIncreasingLengthPath, dfs(matrix, row, col));
            }
        }

        return maxIncreasingLengthPath;
    }


    private int dfs(int[][] matrix, int row, int col){
        if(cache[row][col] != 0) return cache[row][col];


        //just include this cell
        int maxIncreasingLengthPath = 1;

        for(int[] dir : DIRECTIONS){
            int adjRow = row + dir[0];
            int adjCol = col + dir[1];

            //outside the grid, ignore it
            if(adjRow < 0 || adjRow >= rows || adjCol < 0 || adjCol >= cols) continue;

            //not greater than current cell, ignore it
            if(matrix[adjRow][adjCol] <= matrix[row][col]) continue;


            maxIncreasingLengthPath = Math.max(maxIncreasingLengthPath, dfs(matrix, adjRow, adjCol) + 1);
        }

        cache[row][col] = maxIncreasingLengthPath;

        return cache[row][col];
    }

}
