class Solution {
    public int answer(int[][] a,int i,int j, int dp[][],int pre){
        if(i<0 || j<0 || i>=a.length || j>=a[i].length)
            return -1;
        if(a[i][j]<=pre)    return -1;
        if(dp[i][j]!=-1)    return dp[i][j];
        int temp = 1;
        temp = Math.max(temp,answer(a,i-1,j,dp,a[i][j])+1);
        temp = Math.max(temp,answer(a,i,j-1,dp,a[i][j])+1);
        temp = Math.max(temp,answer(a,i+1,j,dp,a[i][j])+1);
        temp = Math.max(temp,answer(a,i,j+1,dp,a[i][j])+1);
        dp[i][j] = temp;
        return temp;
    }
    public int longestIncreasingPath(int[][] a) {
        if(a.length==0) return 0;
        int dp[][] = new int[a.length][a[0].length];
        for(int i=0;i<a.length;i++){
            for(int j=0;j<a[i].length;j++){
                dp[i][j] = -1;
            }
        }
        int max = 0;
        for(int i=0;i<a.length;i++){
            for(int j=0;j<a[i].length;j++){
                max = Math.max(max,answer(a,i,j,dp,Integer.MIN_VALUE));
            }
        }
        return max;
    }
}
