class Solution {
public:
    int longestIncreasingPath(vector<vector<int>>& matrix) {
        //If matrix is empty, longest possible path is 0
        if(matrix.empty()) return 0;

        //Make a hash matrix to store calculated dfs values for hash[i][j]
        //hash is 2D vector of size m x n
        int m = matrix.size(), n = matrix[0].size();
        vector<vector<int>> hash(m, vector<int>(n, 0));

        //Initialize longest path
        int longest = 0;

        //Traverse all points in 2D vector, matrix
        //i in range(0, m), j in range(0, n)
        //longest will either be itself, or a new longer path starting at matrix[i][j]
        //Pass in INT_MIN to dfs calls to ensure all values encountered in matrix are initially larger
        for(int i = 0; i < m; i++)
            for(int j = 0; j < n; j++) longest = max(longest, dfs(matrix, i, j, INT_MIN, hash));

        return longest;
    }

    int dfs(const vector<vector<int>>& matrix, int i, int j, int comp, vector<vector<int>>& hash){
        //Edge cases. Make sure i (rows) and j (cols) are within boundaries
        //If not without boundaries, return zero because no path exists
        if(i < 0 || i >= matrix.size() || j < 0 || j >= matrix[0].size()) return 0;

        //if value at matrix[i][j] > comparison value
            //Check if we have already calculated a path for i,j in hash[i][j]
            //if so return that value, no need to recalculated
            //else call dfs on top, bottom, left, and right points of i,j
            //store the maximum path in hash[i][j]
        if(matrix[i][j] > comp){
            if(hash[i][j]) return hash[i][j];
            int top   = dfs(matrix, i - 1, j, matrix[i][j], hash) + 1;
            int bot   = dfs(matrix, i + 1, j, matrix[i][j], hash) + 1;
            int left  = dfs(matrix, i, j - 1, matrix[i][j], hash) + 1;
            int right = dfs(matrix, i, j + 1, matrix[i][j], hash) + 1;
            hash[i][j] = max(max(top, bot), max(left, right));
            return hash[i][j];
        }

        //if the value in matrix[i][j] is not greater than comp, path ends, return 0
        return 0;
    }
};
```

