    class Solution {
    public:
        int longestIncreasingPath(vector<vector<int>>& matrix) {
            int result=INT_MIN;
            int m=matrix.size();
            if(m==0)  return 0;
            int n=matrix[0].size();
            vector<vector<int>> state(m, vector<int>(n, 0));
            for(int i=0; i<m; i++){
                for(int j=0; j<n; j++){
                    if(state[i][j]==0)
                        dfs(i,j,matrix,state,result);
                }
            }
            return result;
