        if (dp[i][j]) return dp[i][j];
        int l = dfs(mat, i, j - 1, mat[i][j]);
        int r = dfs(mat, i, j + 1, mat[i][j]);
        int u = dfs(mat, i - 1, j, mat[i][j]);
        int d = dfs(mat, i + 1, j, mat[i][j]);
        return dp[i][j] = max({l, r, u, d}) + 1;
