class Solution {
public:

     int dx[5]={0,-1,0,1,0};
    vector<vector<int>>dp;
    void dfs(int x,int y,int n,int m,vector<vector<int>>&mat)
    {
        dp[x][y]=1;
        int val=0;
        for(int k=0;k<4;k++)
        {
            int i=x+dx[k],j=y+dx[k+1];
            if(i<0||i>=n||j<0||j>=m) continue;
            if((mat[i][j]>mat[x][y])&& dp[i][j]==0)
            {
                dfs(i,j,n,m,mat);
            }
            if(mat[i][j]>mat[x][y])
            val=max(val,dp[i][j]);
        }
        dp[x][y]+=val;
    }
    int longestIncreasingPath(vector<vector<int>>& matrix) {
        int n=matrix.size(),m=matrix[0].size();
        int ans=0;
        dp.resize(n,vector<int>(m,0));
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(dp[i][j]==0)
                {
                    dfs(i,j,n,m,matrix);
                }
                ans=max(ans,dp[i][j]);
            }
        }
        return ans;
    }
};

