                     if(x >= 0 && y >= 0 && x < n && y < m && matrix[x][y] > matrix[r][c]) {
                        indegree[x][y]--;
                        if(indegree[x][y] == 0) {
                            queue.add(x * m + y);
                        }
                    }
                }
            }
            level++;
        }
       return level; // Last level is your answer
    }
}
