            sublength = 1
            path_length = 0
            for new_row, new_col in (row + 1, col), (row - 1, col), (row, col + 1), (row, col - 1):
                if 0 <= new_row <= len(matrix) - 1 and 0 <= new_col <= len(matrix[0]) - 1 and matrix[row][col] < matrix[new_row][new_col]:
                    if (new_row, new_col) in visited:
                        path_length = max(path_length, visited[(new_row, new_col)])
                    else:
                        path_length = max(path_length, dfs(new_row, new_col))

            visited[(row, col)] = path_length + sublength
            return path_length + sublength

        for row in range(len(matrix)):
            for col in range(len(matrix[0])):
                if (row, col) not in visited:
                    dfs(row, col)

        return max(visited.values())
