public class Solution {
    private int res = 0;

    public int LongestIncreasingPath(int[][] matrix) {
        if (matrix == null || matrix.Length == 0)
            return res;

        int[,] cache = new int[matrix.Length, matrix[0].Length];

        for (int i = 0; i < matrix.Length; i++)
            for (int j = 0; j < matrix[0].Length; j++)
                DFS(matrix, i, j, 0, cache);

        return res;
    }

    private void DFS(int[][] matrix, int i, int j, int curLen, int[,] cache)
    {
        curLen++;

        if (cache[i, j] >= curLen)
            return;
        else
        {
            cache[i, j] = curLen;
            res = Math.Max(res, curLen);
        }

        if (i - 1 > -1 && matrix[i - 1][j] > matrix[i][j])
            DFS(matrix, i - 1, j, curLen, cache);

        if (j - 1 > -1 && matrix[i][j - 1] > matrix[i][j])
            DFS(matrix, i, j - 1, curLen, cache);

        if (i + 1 < matrix.Length && matrix[i + 1][j] > matrix[i][j])
            DFS(matrix, i + 1, j, curLen, cache);

        if (j + 1 < matrix[0].Length && matrix[i][j + 1] > matrix[i][j])
            DFS(matrix, i, j + 1, curLen, cache);
    }
}

