func longestIncreasingPath(_ matrix: [[Int]]) -> Int {
	if matrix.isEmpty { return 0 }

	let w = matrix[0].count
	let h = matrix.count
	var visiteds = Array(repeating: Array(repeating: false, count: w), count: h)
	var maxCount = 0

	for i in 0..<h {
		for j in 0..<w {
			let num = matrix[i][j]
			let count = lengthOf(matrix, i, j, nil, &visiteds)
			if count > (h*w) / 2 { return count }
			print(count)
			maxCount = max(maxCount, count)
		}
	}

	return maxCount
}

func lengthOf(_ matrix: [[Int]], _ i: Int, _ j: Int, _ prev: Int?, _ visiteds: inout [[Bool]]) -> Int {
	if i < 0 || i >= matrix.count || j < 0 || j >= matrix[0].count { return 0 }
	if visiteds[i][j] { return 0 }

	let cur = matrix[i][j]
	if let prev = prev, cur <= prev { return 0 }

	visiteds[i][j] = true
	let maxLength = 1 + max(lengthOf(matrix, i - 1, j, cur, &visiteds),
							lengthOf(matrix, i + 1, j, cur, &visiteds),
							lengthOf(matrix, i, j - 1, cur, &visiteds),
							lengthOf(matrix, i, j + 1, cur, &visiteds))
	visiteds[i][j] = false

	return maxLength
}
