The idea is similar the the DFS but this approach is more selective and intuitive personally.
* You preprocess and generate an indegree array.
* Then you do a topological sort with bfs to get the result
```
public:
    int R, C;
    bool valid(const int &row, const int &col) {
        return row >= 0 && row < R && col >= 0 && col < C;
    }

    int longestIncreasingPath(vector<vector<int>>& matrix) {
        if(matrix.empty() || !matrix.size())
            return 0;
        R = matrix.size(), C = matrix[0].size();

        vector<vector<int>> indegree(R, vector<int>(C));
        vector<pair<int, int>> dirs{{0, -1}, {0, 1}, {1, 0}, {-1, 0}};
        queue<pair<int, int>> q;

        for(int i = 0; i < R; i++) {
            for(int j = 0; j < C; j++) {
                for(const auto &p: dirs) {
                    int row = i + p.first, col = j + p.second;
                    if(valid(row, col) && matrix[i][j] > matrix[row][col])
                        indegree[i][j]++;
                }
                if(!indegree[i][j])
                    q.push({i, j});
            }
        }

        int len(0);
        while(!q.empty()) {
            int size = q.size();
            while(size--) {
                auto front = q.front();
                q.pop();
                int row = front.first, col = front.second;
                for(const auto &p: dirs) {
                    int nextRow = row + p.first;
                    int nextCol = col + p.second;
                    if(valid(nextRow, nextCol)
                       && matrix[nextRow][nextCol] > matrix[row][col]
                       && !--indegree[nextRow][nextCol])
                        q.push({nextRow, nextCol});
                }
            }
            len++;
        }
        return len;
    }
