        def dfs(i, j):
            if dp[i][j] == 0:
                dp[i][j] = 1
                num = matrix[i][j]
                for di,dj in [(0, 1), (1, 0), (0, -1), (-1, 0)]:
                    r, c = i + di, j + dj
                    if 0 <= r < m and 0 <= c < n:
                        if num > matrix[r][c]:
                            dp[i][j] = max(dp[i][j], 1 + dfs(r, c))
            return dp[i][j]

        if not matrix or not matrix[0]:
