        m, n = len(matrix), len(matrix[0])
        dp = [[0 for _ in range(n)] for _ in range(m)]
        for i in range(m):
            for j in range(n):
                dfs(i, j)
        return max([dp[i][j] for i in range(m) for j in range(n)])
