The basic idea is to record how many inputs a node has.
The number of input nodes of a node is the number of the neighbors which is less (or greater) than it.

After all, this is no more than a normal topological sort algorithm.

    class Solution {
    public:
    	int longestIncreasingPath(const vector<vector<int>>& matrix) {
    		const int h = matrix.size();
    		if (h == 0) return 0;
    		const int w = matrix[0].size();
    		if (w == 0) return 0;
    		vector<int> num_in(w*h, 0);
    		queue<pair<int,int>> q, nq;
    		for (int y = 0; y < h; ++y) {
    			for (int x = 0; x < w; ++x) {
    				int nin = 0;
    				int cur = matrix[y][x];
    				if (x-1 >= 0) { nin += cur < matrix[y][x-1]; }
    				if (x+1 <  w) { nin += cur < matrix[y][x+1]; }
    				if (y-1 >= 0) { nin += cur < matrix[y-1][x]; }
    				if (y+1 <  h) { nin += cur < matrix[y+1][x]; }
    				num_in[w*y+x] = nin;
    				if (nin == 0) {
    					q.emplace(y,x);
    				}
    			}
    		}
    		int l = 0;
    		while (not q.empty()) {
    			while (not q.empty()) {
    				pair<int,int> curpos = q.front();
    				q.pop();
    				int y = curpos.first;
    				int x = curpos.second;
    				int cur = matrix[y][x];
    #define WALK(y, x)\
    	int &nin = num_in[w*(y)+(x)];\
    	if (cur > matrix[y][x] and nin > 0) {\
    		--nin;\
    		if (nin == 0) {\
    			nq.emplace(y, x);\
    		}\
    	}
    				if (x-1 >= 0) { WALK(y,x-1); }
    				if (x+1 <  w) { WALK(y,x+1); }
    				if (y-1 >= 0) { WALK(y-1,x); }
    				if (y+1 <  h) { WALK(y+1,x); }
    #undef WALK
    			}
    			++l;
    			swap(q, nq);
    		}
    		return l;
    	}
    };

