class Solution {
public:
    int r,c;
    int solve(vector<vector<int>>& mat,vector<vector<int>>& dp, int i, int j,int prev)
    {
        // r and c is the size of matrix 
        // i and j is the current indices....
        if(i < 0 || i == r || j < 0 || j == c || mat[i][j] <= prev)
            return 0;

        if(dp[i][j] != 0)
            return dp[i][j];

        int ans = 1;
        ans = max(ans,1 + solve(mat, dp, i + 1, j, mat[i][j]));
        ans = max(ans,1 + solve(mat, dp, i - 1, j, mat[i][j]));
        ans = max(ans,1 + solve(mat, dp, i, j + 1, mat[i][j]));
        ans = max(ans,1 + solve(mat, dp, i, j - 1, mat[i][j]));

        dp[i][j] = ans;
        return dp[i][j];
    }

    int longestIncreasingPath(vector<vector<int>>& matrix) {
         r = matrix.size();
         c = matrix[0].size();

        vector<vector<int>> dp(r,vector<int>(c,0));

        int maxpath = 0;
        for(int i = 0; i < dp.size(); i++)
        {
            for(int j = 0; j < dp[0].size(); j++)
            {
                maxpath = max(maxpath, solve(matrix,dp,i,j,-1));
            }
        }
        return maxpath;
    }
};
