class Solution {
    int dp[201][201];
private:
    int Find(int count, int i, int j, vector<vector<int>> &matrix) {
        int n = matrix[0].size();
        int m = matrix.size();
        count++;

        if(dp[i][j] != -1)
            return dp[i][j];

        //Going to the right
        if((j + 1) >= 0 && (j + 1) < n && matrix[i][j + 1] > matrix[i][j])
            count = max(count, 1 + Find(0, i, j + 1, matrix));

        //Going to the left
        if((j - 1) >= 0 && (j - 1) < n && matrix[i][j - 1] > matrix[i][j])
            count = max(count, 1 + Find(0, i, j - 1, matrix));

        //Going up
        if((i - 1) >= 0 && (i - 1) < m && matrix[i - 1][j] > matrix[i][j])
            count = max(count, 1 + Find(0, i - 1, j, matrix));

        //Going down
        if((i + 1) >= 0 && (i + 1) < m && matrix[i + 1][j] > matrix[i][j])
            count = max(count, 1 + Find(0, i + 1, j, matrix));

        return dp[i][j] = count;
    }

public:
    int longestIncreasingPath(vector<vector<int>>& matrix) {
        int n = matrix[0].size();
        int m = matrix.size();
        int max_length = INT_MIN;

        memset(dp, -1, sizeof dp);

        for(int i = 0; i < m; ++i)
            for(int j = 0; j < n; ++j)
                max_length = max(max_length, Find(0, i, j, matrix));

        for(int i = 0; i < m; ++i) {
            for(int j = 0; j < n; ++j)
                cout << dp[i][j] << " ";
            cout << endl;
        }

        return max_length;
    }
};
