class Solution {
public:
       int solve(vector<vector<int>>& grid,vector<vector<int>>&dp,int i,int j, int prev, vector<vector<int>>& visited){
        if (i < 0 || j < 0 ||j >= grid[0].size() || i >= grid.size() || visited[i][j]==-1 || grid[i][j] <= prev ) return 0;
        if(dp[i][j]!=-1) return dp[i][j];
        visited[i][j] = -1;
        int left  = solve ( grid , dp , i+1,j , grid[i][j] , visited) ;
        int down  = solve ( grid , dp , i-1,j , grid[i][j] , visited) ;
        int right = solve ( grid , dp , i,j+1 , grid[i][j] , visited) ;
        int up    = solve ( grid , dp , i,j-1 , grid[i][j] , visited) ;

        visited[i][j] = 0;

        return dp[i][j] = 1 + max(max(left,right),max(up,down));
    }
    int longestIncreasingPath(vector<vector<int>>& grid) {

      vector<vector<int>> dp(grid.size(), vector<int>(grid[0].size(),-1));
      vector<vector<int>> visited(grid.size(), vector<int>(grid[0].size(),0));

      int maxA = 1;
       for (int i = 0; i < grid.size(); i++)
       {
         for (int j = 0; j < grid[0].size(); j++)
         {
           maxA = max(maxA,solve(grid,dp,i,j,-1,visited));
         }
       }
        return maxA;
    }
};
